#include "gtest/gtest.h"  // access test macro
#include "Longest_Palindromic_Substring.h"


// --------------------------------------------------------------------

TEST(Hash_UnorderedMap, Timeout01){
    string input = "";
    Solution_Hash_UnorderedMap sol;
    ASSERT_EQ(sol.longestPalindrome(input), "ranynar");
}

TEST(Hash_UnorderedMap, Basic01){
    string input = "abb";
    Solution_Hash_UnorderedMap sol;
    ASSERT_EQ(sol.longestPalindrome(input), "bb");
}

TEST(Hash_UnorderedMap, Basic02){
    string input = "bb";
    Solution_Hash_UnorderedMap sol;
    ASSERT_EQ(sol.longestPalindrome(input), "bb");
}

TEST(Hash_UnorderedMap, Timeout02){
    string input = "";
    Solution_Hash_UnorderedMap sol;
    ASSERT_EQ(sol.longestPalindrome(input), "");
}




TEST(Hash_UnorderedMap, Fail01){
    string input = "";
    Solution_Hash_UnorderedMap sol;
    ASSERT_EQ(sol.longestPalindrome(input), input);
}

