#include "gtest/gtest.h"  // access test macro
#include "Substring_with_Concatenation_of_All_Words.h"

bool identical(vector<int> a, vector<int> b){
    if (a.size() != b.size()){
        return false;
    }
    vector<int>::iterator it;
    bool matchFound = false;
    while (a.size() > 0){
        matchFound = false;
        for (it = b.begin(); it != b.end(); it++){
            if (*it == a[0]){
                // found match
                b.erase(it);
                a.erase(a.begin());
                matchFound = true;
                break;
            }
        }
        if (!matchFound){
            return false;
        }
    }
    return true;
}

TEST(LeetCode, Example04){
    string S = "bbcbcbca";
    vector<string> L;
    L.push_back("cb");
    L.push_back("ca");

    vector<int> expected;
    expected.push_back(4);

    Solution sol;
    vector<int> output = sol.findSubstring(S, L);
    ASSERT_TRUE(identical(output, expected));
}

