TEST(LeetCode, Timeout01){
    string S = "";
    vector<string> L;

    for (unsigned int i = 0; i < 200; ++i){
        if (i % 2){
            L.push_back("ab");
        }
        else{
            L.push_back("ba");
        }
        
    }

    vector<int> expected;


    Solution sol;
    vector<int> output = sol.findSubstring(S, L);
    ASSERT_TRUE(identical(output, expected));
}



